PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "users" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "email" varchar NOT NULL, "crypted_password" varchar, "salt" varchar, "name" varchar, "birthday" datetime(6), "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL);
CREATE TABLE IF NOT EXISTS "schema_migrations" ("version" varchar NOT NULL PRIMARY KEY);
CREATE TABLE IF NOT EXISTS "ar_internal_metadata" ("key" varchar NOT NULL PRIMARY KEY, "value" varchar, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL);
CREATE TABLE IF NOT EXISTS "questions" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "content" varchar, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL);
CREATE TABLE IF NOT EXISTS "answers" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "content" text, "question_id" integer NOT NULL, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL, "explain" text, CONSTRAINT "fk_rails_3d5ed4418f"
FOREIGN KEY ("question_id")
  REFERENCES "questions" ("id")
);
CREATE TABLE IF NOT EXISTS "dog_size_types" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL);
CREATE TABLE IF NOT EXISTS "dog_answer_rerationships" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "dog_id" integer NOT NULL, "answer_id" integer NOT NULL, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL, CONSTRAINT "fk_rails_b2474fa37e"
FOREIGN KEY ("dog_id")
  REFERENCES "dogs" ("id")
, CONSTRAINT "fk_rails_c39f657bae"
FOREIGN KEY ("answer_id")
  REFERENCES "answers" ("id")
);
CREATE TABLE IF NOT EXISTS "boards" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar NOT NULL, "body" text NOT NULL, "user_id" integer NOT NULL, "dog_id" integer, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL, "board_image" varchar, CONSTRAINT "fk_rails_cea3a8782e"
FOREIGN KEY ("dog_id")
  REFERENCES "dogs" ("id")
, CONSTRAINT "fk_rails_0732f8ef3d"
FOREIGN KEY ("user_id")
  REFERENCES "users" ("id")
);
CREATE TABLE IF NOT EXISTS "comments" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "body" text NOT NULL, "user_id" integer NOT NULL, "board_id" integer NOT NULL, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL, CONSTRAINT "fk_rails_03de2dc08c"
FOREIGN KEY ("user_id")
  REFERENCES "users" ("id")
, CONSTRAINT "fk_rails_9510ce0f70"
FOREIGN KEY ("board_id")
  REFERENCES "boards" ("id")
);
CREATE TABLE IF NOT EXISTS "dog_countries" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL);
CREATE TABLE IF NOT EXISTS "dogs" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "breed" varchar NOT NULL, "height" varchar DEFAULT NULL, "weight" varchar DEFAULT NULL, "hair_length" integer DEFAULT NULL, "created_at" datetime(6) NOT NULL, "updated_at" datetime(6) NOT NULL, "dog_size_type_id" integer NOT NULL, "dog_image" varchar DEFAULT NULL, "dog_country_id" integer DEFAULT NULL, "activity_level" integer DEFAULT NULL, "solitude_tolerance" integer DEFAULT NULL, "care_difficulty" integer DEFAULT NULL, "catchphrase" text DEFAULT NULL, "features_sentence" text DEFAULT NULL, "personality_sentence" text DEFAULT NULL, "history_sentence" text DEFAULT NULL, "recommended_for_whom" text DEFAULT NULL, CONSTRAINT "fk_rails_4941a4297d"
FOREIGN KEY ("dog_size_type_id")
  REFERENCES "dog_size_types" ("id")
, CONSTRAINT "fk_rails_7954c17b7e"
FOREIGN KEY ("dog_country_id")
  REFERENCES "dog_countries" ("id")
);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "index_users_on_email" ON "users" ("email");
CREATE INDEX "index_answers_on_question_id" ON "answers" ("question_id");
CREATE INDEX "index_dog_answer_rerationships_on_dog_id" ON "dog_answer_rerationships" ("dog_id");
