CREATE UNIQUE INDEX "index_dog_answer_rerationships_on_dog_id_and_answer_id" ON "dog_answer_rerationships" ("dog_id", "answer_id");
CREATE INDEX "index_boards_on_user_id" ON "boards" ("user_id");
CREATE INDEX "index_boards_on_dog_id" ON "boards" ("dog_id");
CREATE INDEX "index_comments_on_user_id" ON "comments" ("user_id");
CREATE INDEX "index_comments_on_board_id" ON "comments" ("board_id");
CREATE INDEX "index_dogs_on_dog_size_type_id" ON "dogs" ("dog_size_type_id");
CREATE INDEX "index_dogs_on_dog_country_id" ON "dogs" ("dog_country_id");
COMMIT;
